mapper.select:
SELECT categoryno, name,image,seq,visible,reg_date
FROM category
where categoryno = #{categoryno}

mapper.update:
        UPDATE cate
        set seqno = (select min(seqno) from cate) - 1
        where cateno = #{cateno}

        UPDATE cate
        set seqno = (select max(seqno) from cate) + 1
        where cateno = #{cateno}

        UPDATE cate
        set seqno = seqno + 1
        where cateno = #{cateno}

        UPDATE cate
        set seqno = seqno - 1
        where cateno = #{cateno}

        UPDATE cate
        set visible = 'N'
        where cateno = #{cateno}

        UPDATE cate
        set visible = 'Y'
        where cateno = #{cateno}

mapper.delete:
DELETE FROM category
WHERE cateno IN

    #{cateno}

