        FROM (
                 SELECT categoryno, name, ROWNUM as r
                 FROM (
                          SELECT categoryno, name
                          FROM category
                          WHERE name LIKE '%' || #{word} || '%'

                      )
                 WHERE ROWNUM <= #{end_num}
             )
