mapper.select:
SELECT COUNT(id) as cnt
FROM customer
WHERE id=#{id}

SELECT COUNT(nickname) as cnt
FROM customer
WHERE nickname=#{nickname}

SELECT custno, id, passwd, cname,grade,phone,address1,address2
FROM customer
WHERE id = #{id}

mapper.update:
UPDATE customer
SET  cname=#{cname}, phone=#{phone},  address1=#{address1}, address2=#{address2}
WHERE id=#{id}

mapper.select:

    SELECT COUNT(custno) as cnt
    FROM customer
    WHERE id = #{id}
      AND passwd = #{passwd}
      AND grade <> 99

SELECT COUNT(customerno) as cnt
FROM customer
WHERE customerno=#{customerno} AND passwd=#{passwd}

mapper.update:
UPDATE customer
SET passwd=#{passwd}
WHERE customerno=#{customerno}
