mapper.select:
SELECT COUNT(id) as cnt
FROM owner
WHERE id=#{id}

if.if:
  
  
  SELECT restno,name,tel,categoryno,image1,image2,image3
  FROM (
    SELECT re.restno, re.name, re.tel, re.categoryno, ri1.imagefile AS image1, ri2.imagefile AS image2, ri3.imagefile AS image3, ROWNUM AS rnum
    FROM restaurant re
    LEFT JOIN (
        SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn
        FROM restimage
    ) ri1 ON re.restno = ri1.restno AND ri1.rn = 1
    LEFT JOIN (
        SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn
        FROM restimage
    ) ri2 ON re.restno = ri2.restno AND ri2.rn = 2
    LEFT JOIN (
        SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn
        FROM restimage
    ) ri3 ON re.restno = ri3.restno AND ri3.rn = 3
    WHERE ROWNUM <= #{end_num}
  )
  WHERE rnum >= #{start_num}

  
  
