mapper.select:
SELECT restno, name, tel, grade, address, lat, lng, reserve_range, ownerno, categoryno, botareano
FROM restaurant
WHERE restno = #{restno}

SELECT restno, name, tel, grade, address, lat, lng, reserve_range, ownerno, categoryno, botareano FROM restaurant WHERE restno IN ( SELECT restno FROM admitperson WHERE reserve_date = TO_DATE(#{date}, 'YYYY-MM-DD HH24:MI:SS') AND time = #{time} AND admit_person >= curr_person + #{person})
AND categoryno = #{categoryno}
AND botareano IN
#{item}
AND restno IN ( SELECT restno FROM menu WHERE price BETWEEN #{min_price} * 10000 AND #{max_price} * 10000)

SELECT restno, name, tel, grade, address, lat, lng, reserve_range, ownerno, categoryno, botareano, image1,image2,image3 FROM( SELECT re.restno, name, tel, grade, address, lat, lng, reserve_range, ownerno, categoryno, botareano, ri1.imagefile AS image1, ri2.imagefile AS image2, ri3.imagefile AS image3, ROWNUM AS rnum FROM restaurant re LEFT JOIN ( SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn FROM restimage ) ri1 ON re.restno = ri1.restno AND ri1.rn = 1 LEFT JOIN ( SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn FROM restimage ) ri2 ON re.restno = ri2.restno AND ri2.rn = 2 LEFT JOIN ( SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn FROM restimage ) ri3 ON re.restno = ri3.restno AND ri3.rn = 3 WHERE re.restno IN ( SELECT restno FROM admitperson WHERE reserve_date = TO_DATE(#{date}, 'YYYY-MM-DD HH24:MI:SS') AND time = #{time} AND admit_person >= curr_person + #{person})
AND categoryno = #{categoryno}
AND botareano IN
#{item}
AND re.restno IN ( SELECT restno FROM menu WHERE price BETWEEN #{min_price} * 10000 AND #{max_price} * 10000) )

SELECT restno, name, tel, grade, address, lat, lng, reserve_range, ownerno, categoryno, botareano, image1,image2,image3
FROM(
    SELECT re.restno, name, tel, grade, address, lat, lng, reserve_range, ownerno, categoryno, botareano, ri1.imagefile AS image1, ri2.imagefile AS image2, ri3.imagefile AS image3, ROWNUM AS rnum
    FROM restaurant re
    LEFT JOIN (
      SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn
      FROM restimage 
    ) ri1 ON re.restno = ri1.restno AND ri1.rn = 1
    LEFT JOIN (
      SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn
      FROM restimage
    ) ri2 ON re.restno = ri2.restno AND ri2.rn = 2
    LEFT JOIN (
      SELECT restno, imagefile, ROW_NUMBER() OVER (PARTITION BY restno ORDER BY rest_imgno) AS rn
      FROM restimage
    ) ri3 ON re.restno = ri3.restno AND ri3.rn = 3
)
WHERE lat BETWEEN #{westLat} AND #{eastLat}
AND lng BETWEEN #{southLng} AND #{northLng}
