mapper.insert:
INSERT INTO restimage(rest_imgno, imagefile, thumbfile, restno
                   )
VALUES (restimg_seq.nextval, #{imagefile}, #{thumbfile}, #{restno})

mapper.update:
UPDATE restimage
SET
    image = #{image},
    thumbnail = #{thumbnail}
WHERE restno = #{restno} AND id = #{id}

mapper.select:
SELECT id, image, thumbnail, restno
FROM restimage
WHERE restno = #{restno}

SELECT rest_imgno,imagefile
FROM restimage
WHERE imagefile =#{imagefile}
